import { useState } from "react";

const SimpleInput = (props) => {

  const [name,setName]=useState("");
  const [nameTouched,setNameTouched]=useState(false);

  const nameValid=name.trim() !== "";
  const nameInputInvalid=nameTouched && !nameValid;

  let formIsValid=false;

  if(nameValid){
    formIsValid=true;
  }

  const handleSubmit=(event)=>{
    event.preventDefault();

    setNameTouched(true);

    if(!nameValid){
      return;
    }

    console.log(name);
    setName("");
    setNameTouched(false);

  };

  const handleBlur=(event)=>{
    setNameTouched(true);
  };

  const handleChange=(event)=>{
    setName(event.target.value);
  };

  const classNames=nameInputInvalid ? "form-control invalid" : "form-control" ;

  return (
    <form onSubmit={handleSubmit}>
      <div className={classNames}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' value={name} onBlur={handleBlur} onChange={handleChange} />
        {nameInputInvalid && <p className="error-text">Name must not be empty!</p>}
      </div>
      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
